package com.example.thecanon001.webir.activity;

import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.support.v7.widget.Toolbar;
import android.widget.Toast;
import com.example.thecanon001.webir.R;
import com.example.thecanon001.webir.adapter.CarViewAdapter;
import com.example.thecanon001.webir.entity.Car;
import com.example.thecanon001.webir.model.ServiceFactoryProvider;

import java.util.ArrayList;


import butterknife.BindView;
import butterknife.ButterKnife;
import butterknife.OnClick;

public class MainActivity extends AppCompatActivity {
    private int count = 0;
    private long startMillis=0;
    private boolean stub = true;
    private ArrayList<Car> carList = new ArrayList<Car>();
    private CarViewAdapter cardViewAdapter;
    private ArrayList<Car> listJillKing;
    private String joaking;
    private String jill;
    private boolean change = false;

    @BindView(R.id.recycler_view)
    protected RecyclerView recyclerView;
    @BindView(R.id.toolbar)
    protected Toolbar toolbar;


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        ButterKnife.bind(this);
        setUpView();
        init();
        listJillKing = new ArrayList<>();
        for(Car car: carList) {
            Car car2 = new Car();
            car2.setImage(car.getImage());
            car2.setModel(car.getModel());
            car2.setPrice(car.getPrice());
            car2.setLocation(car.getLocation());
            car2.setKm(car.getKm());
            listJillKing.add(car2);
        }
        jill = "";
        joaking = "";
    }

    private void setUpView() {
        setSupportActionBar(toolbar);
        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(getApplicationContext());
        cardViewAdapter = new CarViewAdapter(carList);
        recyclerView.setLayoutManager(linearLayoutManager);
        recyclerView.setAdapter(cardViewAdapter);
    }

    private void init() {
        carList = ServiceFactoryProvider.getServiceFactory(stub).getCarService().getCarList(getApplication());
        cardViewAdapter.updateListView(carList);
    }

    @OnClick(R.id.fab)
    public void onTouchFab(){
        if(!change) {
            change = true;
            for(int i = 0; i<carList.size();i++) {
                if(i%2 == 0) {
                    carList.get(i).setImage(joaking);
                    carList.get(i).setModel("Joaquincito");
                    carList.get(i).setKm("0 km");
                    carList.get(i).setLocation("A 2 cuadras de la fing");
                    carList.get(i).setPrice("precio incalculable");
                }
                else {
                    carList.get(i).setImage(jill);
                    carList.get(i).setModel("Jill Smith");
                    carList.get(i).setKm("Disfruta de coquetear");
                    carList.get(i).setPrice("cada hombre");
                    carList.get(i).setLocation("que encuentra ");
                }
            }
        }
        else {
            change = false;
            carList = new ArrayList<>();
            for(Car car: listJillKing) {
                Car car2 = new Car();
                car2.setImage(car.getImage());
                car2.setModel(car.getModel());
                car2.setPrice(car.getPrice());
                car2.setLocation(car.getLocation());
                car2.setKm(car.getKm());
                carList.add(car2);
            }
        }
        cardViewAdapter.updateListView(carList);
    }

    @OnClick(R.id.toolbar)
    public void onTouchToolbar(){
        long time= System.currentTimeMillis();
        //if it is the first time, or if it has been more than 3 seconds since the first tap ( so it is like a new try), we reset everything
        if (startMillis==0 || (time-startMillis> 3000) ) {
            startMillis=time;
            count=1;
        }
        //it is not the first, and it has been  less than 3 seconds since the first
        else{ //  time-startMillis< 3000
            count++;
        }
        if(count == 5){
            if(!stub) {
                stub = true;
                Toast.makeText(getApplicationContext(), "Se activa Stub", Toast.LENGTH_SHORT).show();
            }
            else {
                stub = false;
                Toast.makeText(getApplicationContext(),"Se desactiva Stub", Toast.LENGTH_SHORT).show();
            }
            init();
        }
    }
}
